import React, { useState } from 'react';
import { auth, provider } from './firebase';
import { signInWithPopup } from 'firebase/auth';

function App() {
  const [user, setUser] = useState(null);
  const [companies, setCompanies] = useState([]);
  const [companyName, setCompanyName] = useState("");
  const [type, setType] = useState("SaaS");

  const login = async () => {
    const result = await signInWithPopup(auth, provider);
    setUser(result.user);

    // Backend'e oyuncu kaydı
    await fetch("http://localhost:5000/api/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ uid: result.user.uid, name: result.user.displayName })
    });
  };

  const addCompany = async () => {
    const res = await fetch("http://localhost:5000/api/addCompany", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ uid: user.uid, companyName, type })
    });
    const data = await res.json();
    setCompanies(data.companies);
    setCompanyName("");
  };

  const loadCompanies = async () => {
    const res = await fetch(`http://localhost:5000/api/companies/${user.uid}`);
    const data = await res.json();
    setCompanies(data.companies);
  };

  return (
    <div style={{ padding: 20 }}>
      {!user ? <button onClick={login}>Login with Google</button> : (
        <>
          <h1>Welcome, {user.displayName}</h1>
          <input value={companyName} onChange={e => setCompanyName(e.target.value)} placeholder="Company Name"/>
          <select value={type} onChange={e => setType(e.target.value)}>
            <option>SaaS</option>
            <option>E-commerce</option>
            <option>Game Studio</option>
          </select>
          <button onClick={addCompany}>Add Company</button>
          <button onClick={loadCompanies}>Load Companies</button>
          <ul>
            {companies.map(c => <li key={c.id}>{c.name} ({c.type}) - Profit: {c.netProfit}</li>)}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
